import { useState, useEffect } from 'react';
import { Col, Container, Row } from 'react-bootstrap';
import { ArrowRightCircle } from 'react-bootstrap-icons';
import headerImg from "../../assets/img/DVD_logo.svg";
import 'animate.css';
import TrackVisibility from 'react-on-screen';
import './Banner.css';

const Banner = () => {
  const toRotate = ['Front Dev', 'React Developer', 'Web Developer'];
  const [loopNum, setLoopNum] = useState(0);
  const [isDeleting, setIsDeleting] = useState(false);
  const [text, setText] = useState('');
  const [delta, setDelta] = useState(300 - Math.random() * 100);
  const period = 2000;
  const tick = () => {
    let i = loopNum % toRotate.length;
    let fullText = toRotate[i];
    let updateText = isDeleting ? fullText.substring(0, text.length - 1) : fullText.substring(0, text.length + 1);

    setText(updateText);
    if (isDeleting) {
      setDelta(prevDelta => prevDelta / 2);
    }

    if (!isDeleting && updateText === fullText) {
      setIsDeleting(true);
      setDelta(period);
    } else if (isDeleting && updateText === '') {
      setIsDeleting(false);
      setLoopNum(loopNum + 1);
      setDelta(500);
    }
  };

  useEffect(() => {
    let ticker = setInterval(() => {
      tick();
    }, delta);

    return () => clearInterval(ticker);
  });

  return (
    <section className='banner' id="home">
      <Container>
        <Row className="align-items-center">
          <Col
            xs={12}
            md={6}
            xl={7}
          >
            <TrackVisibility>
            {({ isVisible }) =>
              <div className={isVisible ? 'animate__animated animate__pulse' : ''}>
                <span className="tagline">
                  Welcome to my Portfolio
                </span>
                <h1>
                  {`Hi, I'm `}<span className="wrap">{text}</span>
                </h1>
                <p>Lorem, ipsum dolor sit amet consectetur adipisicing elit. Vero, corrupti.</p>
                <button onClick={() => console.log('connected')}>Let's Connect <ArrowRightCircle size={25}/></button>
              </div>
            }
            </TrackVisibility>
          </Col>

          <Col
            xs={12}
            md={6}
            xl={5}
            className="header-img-div"
          >
            <img src={headerImg} alt="Header Img" />
          </Col>
        </Row>
      </Container>
    </section>
  );
};

export default Banner;
